import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Button, Table, Input, Form } from 'semantic-ui-react'
import { Link } from 'react-router-dom';




export default function Read() {

    const [APIData, setAPIData] = useState([]);
    useEffect(() => {
        axios.get('http://localhost:8000/get/')
            .then((response) => {
                setAPIData(response.data)
            })
    }, [])

    const setData = (data) => {
        console.log(data)
    }
    return (
        <div>
           <Input type='text'>
            </Input>
            <Form>
            <Table singleLine>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>Name</Table.HeaderCell>
                        <Table.HeaderCell>Registration Date</Table.HeaderCell>
                        <Table.HeaderCell>Department</Table.HeaderCell>
                        <Table.HeaderCell>Active</Table.HeaderCell>
                        <Table.HeaderCell>Update</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>

                <Table.Body>
                    {APIData.map((data) => {
                        return (
                            <Table.Row>
                                <Table.Cell><input type='text' value={data.first_name} onChange={console.log('changed')}></input></Table.Cell>
                                <Table.Cell>{data.last_name}</Table.Cell>
                                <Table.Cell>{data.department}</Table.Cell>
                                <Table.Cell>No</Table.Cell>
                                <Link to='/update'>
                                    <Table.Cell>
                                        <Button onClick={()=>setData(data)}>Update</Button>
                                    </Table.Cell>
                                </Link>
                            </Table.Row>


                        )
                    })
                    }
                </Table.Body>
                </Table>
                </Form>
            {/* <table>
                <th>
                    <tr>
                        <td>
                            name
                        </td>
                    </tr>
                </th>
                <tbody>
                    {
                        APIData.map((data => {
                            return (
                                <tr>
                                    <td>
                                        <input type='text' value={data.first_name}>

                                        </input>
                                    </td>
                                </tr>
                            )
                        }))
                    }
                </tbody>
            </table> */}
        </div>
    )
}
