import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Button, Table, Input, Form } from 'semantic-ui-react'
import { Link } from 'react-router-dom';




export default function Read() {

    const [APIData, setAPIData] = useState([]);
    useEffect(() => {
        axios.get('http://localhost:8000/get/')
            .then((response) => {
                setAPIData(response.data)
            })
    }, [])


    function handleDelete(data) {
        console.log(data)
    }
    const setData = (data) => {
        console.log(data)
    }
    return (
        <div>
            <Form>
                <Table singleLine>
                    <Table.Header>
                        <Table.Row>
                            <Table.HeaderCell>Name</Table.HeaderCell>
                            <Table.HeaderCell>Registration Date</Table.HeaderCell>
                            <Table.HeaderCell>Department</Table.HeaderCell>
                            <Table.HeaderCell>Active</Table.HeaderCell>
                            {/* <Table.HeaderCell>Update</Table.HeaderCell> */}
                        </Table.Row>
                    </Table.Header>

                    <Table.Body>
                        {APIData.map((data) => {
                            return (
                                <Table.Row>
                                    <Table.Cell><Input type='text' defaultValue={data.first_name}></Input></Table.Cell>
                                    <Table.Cell><Input type='text' defaultValue={data.last_name}></Input></Table.Cell>
                                    <Table.Cell>{data.department}</Table.Cell>
                                    <Table.Cell>No</Table.Cell>
                                    <Table.Cell>
                                        <Button onClick={(e) => setData(data)}>Update</Button>
                                    </Table.Cell>
                                    <Table.Cell>
                                        <Button onClick={(e) =>handleDelete(data) }>Delete</Button>
                                    </Table.Cell>
                                </Table.Row>


                            )
                        })
                        }
                    </Table.Body>
                </Table>
            </Form>
            {/* <table>
                <th>
                    <tr>
                        <td>
                            name
                        </td>
                    </tr>
                </th>
