                    {
                        APIData.map((data => {
                            return (
                                <tr>
                                    <td>
                                        <input type='text' value={data.first_name}>

                                        </input>
                                    </td>
                                </tr>
                            )
                        }))
                    }
                </tbody>
            </table> */}
        </div>
    )
}
